import { Box, Center, Flex, HStack, Spacer, Text, useColorModeValue } from "@chakra-ui/react";
import { ColorModeSwitcher} from "./ColorModeSwitcher";

const Header = (): JSX.Element => {

    //use font color based on color mode
    const fontColor = useColorModeValue("mode.text.light", "mode.text.dark");

    return (
        <Center>
            <Flex alignContent="center" padding={5}>
                <Box>
                    // Text with fontSize 5xl and bold and font color based on color mode
                    <Text fontSize="5xl" fontWeight="bold" color={fontColor}>Transcribe</Text>
                </Box>
            </Flex>
            <ColorModeSwitcher />
        </Center>

    );
};

export default Header;
